mapper.select:
SELECT M.RNUM , M.* FROM (SELECT ROWNUM RNUM , V.* FROM (SELECT A.LOCATION_CODE , A.LOCATION_NAME , A.LOCATION_INFO , A.LOCATION_IMG , A.GUIDE_TIP , A.VISIT_MONTH , A.VISIT_NAME , B.ACTI_NUM , B.ACTI_NAME , B.LOCATION , B.PRICE , B.START_DATE , B.END_DATE , B.ACTI_TIP , B.ACTI_IMG , B.USER_ID , B.ACTI_IMG2 , B.ACTI_IMG3 , C.ACTI_REVIEW_STAR , C.ACTI_REVIEW_REC , D.SECOND_TYPE_NAME FROM LOCATION A LEFT JOIN ACTIVITY_INFO B ON (A.LOCATION_CODE = B.LOCATION_CODE) LEFT JOIN ACTI_REVIEW C ON (B.ACTI_NUM = C.ACTI_NUM) LEFT JOIN ACTI_TYPE D ON(B.ACTI_TYPE_NUM = D.ACTI_TYPE_NUM) WHERE A.LOCATION_NAME = #{ locationName } )V
) M
(M.RNUM BETWEEN #{ startRow } and #{ endRow } )
AND M.PRICE BETWEEN #{ price1 } AND #{ price2 }
AND M.END_DATE > #{ date }
AND EXISTS (SELECT ROUND(AVG(C.ACTI_REVIEW_STAR),1) FROM ACTI_REVIEW C JOIN ACTIVITY_INFO B ON (C.ACTI_NUM = B.ACTI_NUM) ) ORDER BY M.ACTI_REVIEW_STAR DESC
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
ORDER BY M.ACTI_REVIEW_REC
ORDER BY M.PRICE ASC
ORDER BY M.START_DATE DESC

