SELECT M.RNUM , M.ACTI_NAME , M.PRICE , M.START_DATE , M.END_DATE , M.ACTI_TIP , M.ACTI_IMG , M.ACTI_TYPE_NUM , M.ACTI_REVIEW_STAR , M.ACTI_REVIEW_REC , M.SECOND_TYPE_NAME FROM (SELECT ROWNUM RNUM , V.ACTI_NAME , V.PRICE , V.START_DATE , V.END_DATE , V.ACTI_TIP , V.ACTI_IMG , V.ACTI_TYPE_NUM , V.ACTI_REVIEW_STAR , V.ACTI_REVIEW_REC , V.SECOND_TYPE_NAME FROM (SELECT B.ACTI_NAME , B.PRICE , B.START_DATE , B.END_DATE , B.ACTI_TIP , B.ACTI_IMG , B.ACTI_TYPE_NUM , C.ACTI_REVIEW_STAR , C.ACTI_REVIEW_REC , D.SECOND_TYPE_NAME FROM LOCATION A LEFT JOIN ACTIVITY_INFO B ON (A.LOCATION_CODE = B.LOCATION_CODE) LEFT JOIN ACTI_REVIEW C ON (B.ACTI_NUM = C.ACTI_NUM) LEFT JOIN ACTI_TYPE D ON(B.ACTI_TYPE_NUM = D.ACTI_TYPE_NUM) WHERE A.LOCATION_NAME LIKE '%' || #{ searchValue } || '%' OR B.ACTI_NAME LIKE '%' || #{ searchValue } || '%') V
)M
M.RNUM BETWEEN #{ startRow } AND #{ endRow }
AND M.PRICE BETWEEN #{ price1 } AND #{ price2 }
AND M.END_DATE > #{ date }
AND EXISTS (SELECT ROUND(AVG(C.ACTI_REVIEW_STAR),1) FROM ACTI_REVIEW C JOIN ACTIVITY_INFO B ON (C.ACTI_NUM = B.ACTI_NUM) ) ORDER BY M.ACTI_REVIEW_STAR DESC
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
