#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
AND M.SECOND_TYPE_NAME IN
#{ arr }
ORDER BY M.ACTI_REVIEW_REC
