mapper.update:
UPDATE MT_FIND
   SET MT_FIND_REP_YN = #{ repYn }
 WHERE MT_FIND_NUM = #{ num }

UPDATE COMENT
   SET MT_COM_REP_YN = #{ repYn }
 WHERE MT_COM_NUM = #{ num }

UPDATE MT_REVIEW
   SET MT_REVIEW_TITLE = #{ title }
   	 , MT_REVIEW_CON = #{ content }

   	 , MT_REVIEW_IMG1 = #{ img1 }


   	 , MT_REVIEW_IMG2 = #{ img2 }


   	 , MT_REVIEW_IMG3 = #{ img3 }

 WHERE MT_REVIEW_NUM = #{ num }

UPDATE MT_REVIEW
   SET MT_REVIEW_REP_YN = #{ repYn }
 WHERE MT_REVIEW_NUM = #{ num }

UPDATE MT_REVIEW
   SET MT_REVIEW_REC = #{ recommend }
 WHERE MT_REVIEW_NUM = #{ num }

UPDATE MT_REVIEW
   SET MT_REVIEW_REP_YN = #{ repYn }
 WHERE MT_REVIEW_NUM = #{ num }

mapper.select:
SELECT C.RNUM , C.MT_REVIEW_NUM , C.MT_REVIEW_TITLE , C.MT_REVIEW_IMG1 , C.MT_REVIEW_CON , C.MT_REVIEW_DATE , C.MT_REVIEW_REP_YN , C.USER_ID , C.MT_REVIEW_IMG2 , C.MT_REVIEW_IMG3 , C.MT_REVIEW_REC FROM (SELECT ROWNUM RNUM , B.MT_REVIEW_NUM , B.MT_REVIEW_TITLE , B.MT_REVIEW_IMG1 , B.MT_REVIEW_CON , B.MT_REVIEW_DATE , B.MT_REVIEW_REP_YN , B.USER_ID , B.MT_REVIEW_IMG2 , B.MT_REVIEW_IMG3 , B.MT_REVIEW_REC FROM (SELECT A.MT_REVIEW_NUM , A.MT_REVIEW_TITLE , A.MT_REVIEW_IMG1 , A.MT_REVIEW_CON , A.MT_REVIEW_DATE , A.MT_REVIEW_REP_YN , A.USER_ID , A.MT_REVIEW_IMG2 , A.MT_REVIEW_IMG3 , A.MT_REVIEW_REC FROM MT_REVIEW A WHERE A.MT_REVIEW_REP_YN IN ('N','L') ORDER BY A.MT_REVIEW_DATE DESC) B
)C WHERE (C.RNUM BETWEEN #{ startRow } and #{ endRow })
AND C.MT_REVIEW_TITLE LIKE '%' || #{ searchValue } || '%'

mapper.insert:
		INSERT
		  INTO MT_REVIEW A
		(
		  A.MT_REVIEW_NUM
		, A.MT_REVIEW_TITLE
		, A.MT_REVIEW_IMG1
		, A.MT_REVIEW_CON
		, A.MT_REVIEW_DATE
		, A.MT_REVIEW_REP_YN
		, A.USER_ID
		, A.MT_REVIEW_IMG2
		, A.MT_REVIEW_IMG3
		, A.MT_REVIEW_REC
		)
		VALUES
		(
		  'MT_REVIEW' || SEQ_MT_REVIEW_NUM.NEXTVAL
        , #{ title }
        , #{ img1 }
        , #{ content }
        , default
        , #{ repYn }
        , #{ userId } 
        , #{ img2 }
        , #{ img3 }
        , #{ recommend }
		)
